class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        ans = 0
        prevdiff = 0
        for i in range(n):
            diff = nums[i]-target[i]

            if prevdiff > 0 and diff > 0:
                if diff > prevdiff:
                    ans += diff - prevdiff
                prevdiff = diff
            elif prevdiff < 0 and diff < 0:
                if abs(diff) > abs(prevdiff):
                    ans += abs(diff) - abs(prevdiff)
                prevdiff = diff
            else:
                ans += abs(diff)
                prevdiff = diff

        return ans
