class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = map(sub, target, nums)
        total = 0
        for sign, group in groupby(diffs, key=signfunc):
            if sign == 1:
                lo = hi = count = next(group)
                total += count
                for x in group:
                    if x > hi:
                        total += x - hi
                    if x < lo:
                        lo = x
                    hi = x
            elif sign == -1:
                lo = hi = count = next(group)
                total -= count
                for x in group:
                    if x < lo:
                        total += lo - x
                    if x > hi:
                        hi = x
                    lo = x
        return total

def signfunc(a):
    return (a > 0) - (a < 0)
