class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        @cache
        def solve(curr,t):
            if curr>=len(nums):
                return 0

            if t>=0:
                if nums[curr]+t==target[curr]:
                    return solve(curr+1,t)
                elif nums[curr]+t>target[curr]:
                    if target[curr]>nums[curr]:
                        return solve(curr+1,target[curr]-nums[curr])
                    else:
                        return nums[curr]-target[curr]+solve(curr+1,target[curr]-nums[curr])
                else:
                    return target[curr]-nums[curr]-t+solve(curr+1,target[curr]-nums[curr])
            else:
                if nums[curr]+t==target[curr]:
                    return solve(curr+1,t)
                elif nums[curr]+t<target[curr]:
                    if target[curr]<nums[curr]:
                        return solve(curr+1,target[curr]-nums[curr])
                    else:
                        return target[curr]-nums[curr]+solve(curr+1,target[curr]-nums[curr])
                else:
                    return abs(target[curr]-nums[curr])+t+solve(curr+1,target[curr]-nums[curr])

        return solve(0,0)
