class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long u = 0, v = 0;
        int n = nums.size();
        vector<long long> a(n + 1, 0), s(n + 1, 0);
        for(int i = 1; i <= n; i ++) a[i] = nums[i - 1] - target[i - 1]; 
        for(int i = 1; i <= n; i ++) {
            s[i] = a[i] - a[i - 1];
            if(s[i] > 0) u += s[i];
            else if(s[i] < 0) v += -s[i];
        }
        return max(u, v);
    }
};
