class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = nums[0] - target[0]
        res = abs(diff)
        last = diff
        for i in range(1, len(nums)):
            diff = nums[i] - target[i]
            if diff >= 0 and last >= 0:
                res += max(0, diff - last)
            elif diff < 0 and last < 0:
                res += max(-1 * (diff - last), 0)
            else:
                res += abs(diff)
                # print(res)
            last = diff
        return res
