class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = []
        n = len(target)
        for i in range(n):
            diff.append(target[i] - nums[i])

        lst = []
        cur = []
        for i in range(n):
            if not cur:
                if diff[i] == 0:
                    continue
                else:
                    cur.append(diff[i])
            else:
                if diff[i] == 0:
                    lst.append(cur)
                    cur = []
                elif diff[i] > 0:
                    if cur[-1] > 0:
                        cur.append(diff[i])
                    else:
                        lst.append(cur)
                        cur = [diff[i]]
                elif diff[i] < 0:
                    if cur[-1] < 0:
                        cur.append(diff[i])
                    else:
                        lst.append(cur)
                        cur = [diff[i]]
        if cur:
            lst.append(cur)
        ans  = 0
        for l in lst:
            if l[0] < 0:
                for i in range(len(l)):
                    l[i] = -l[i]
            steps = l[0]
            for i in range(1, len(l)):
                if l[i] > l[i - 1]:
                    steps += l[i] - l[i - 1]
            ans += steps

        return ans


