class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ans = 0
        positive = True
        curr_operation = 0
        for i in range(len(nums)):
            nums[i] = target[i] - nums[i]
        print(nums)
        for diff in nums:
            if diff > 0 and not positive:
                curr_operation = 0
                positive = True
            elif diff < 0 and positive:
                curr_operation = 0
                positive = False
            elif diff == 0:
                curr_operation = 0
            if curr_operation < abs(diff):
                ans += (abs(diff) - curr_operation)
            curr_operation = abs(diff)

        return ans
