class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        l = [nums[i] - target[i] for i in range(n)]
        res, a = 0, 0
        for i in range(n):
            if l[i]: 
                if a*l[i]>0:
                    if abs(a)<abs(l[i]): res += abs(l[i]-a)
                else: res += abs(l[i])
            a = l[i]

        return res
