class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int tmp = 0;
        int n = nums.size();
        int flag = 0;
        for (int i = 0; i < n; ++i) {
            if (nums[i] > target[i]) {
                if (flag != 1) {
                    ans += tmp;
                    tmp = nums[i] - target[i];
                    flag = 1;
                } else {
                    int cur = nums[i] - target[i];
                    if (cur > tmp) {
                        tmp = cur;
                    } else if (cur < tmp) {
                        ans += tmp - cur;
                        tmp = cur;
                    }
                }
            } else if (nums[i] < target[i]) {
                if (flag != -1) {
                    ans += tmp;
                    tmp = -(nums[i] - target[i]);
                    flag = -1;
                } else {
                    int cur = - (nums[i] - target[i]);
                    if (cur > tmp) {
                        tmp = cur;
                    } else if (cur < tmp) {
                        ans += tmp - cur;
                        tmp = cur;
                    }
                }
            } else {
                flag = 0;
            }
        }
        ans += tmp;
        return ans;
    }
};
