from typing import List


class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [t - n for t, n in zip(target, nums)]
        new = [0]
        pre = 0
        for d in diff:
            if d * pre < 0:
                new.append(0)
            new.append(abs(d))
            pre = d
        new.append(0)
        ans = 0
        up = True
        pre = 0
        memo = 0
        for num in new:
            if num > pre:
                if up:
                    pre = num
                else:
                    memo = pre
                    up = True
                    pre = num
            elif num < pre:
                if not up:
                    pre = num
                else:
                    ans += pre - memo
                    up = False
                    pre = num
        return ans
