class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for(int i=0;i<n;i++){
            nums[i]=target[i]-nums[i];
        }

        long long cnt=0;
        int i=0;
        while(i<n){
            if(nums[i]==0){
                i++;
                continue;
            }

            int j=i;
            if(nums[i]>0){
                int mini=INT_MAX;
                while(j<n && nums[j]>0){
                    mini=min(mini,nums[j]);
                    j++;
                }
                if(mini==INT_MAX) mini=nums[i];
                for(int k=i;k<j;k++){
                    nums[k]=nums[k]-mini;
                }
                cnt+=mini;
            }
            else{
                int maxi=INT_MIN;
                while(j<n && nums[j]<0){
                    maxi=max(maxi,nums[j]);
                    j++;
                }
                if(maxi==INT_MIN) maxi=nums[i];
                for(int k=i;k<j;k++){
                    nums[k]=nums[k]-maxi;
                }
                cnt-=maxi;
            }
        }

        return cnt;
    }
};
