class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] sub = new int[n];
        for (int i = 0; i < n; i++) {
            sub[i] = target[i] - nums[i];
        }
        long ans = 0;
        if (sub[0] > 0) ans += sub[0];
        if (sub[n-1] < 0) ans -= sub[n-1];
        for (int i = 1; i < n; i++) {
            int gap = sub[i] - sub[i-1];
            if (gap > 0) ans += gap;
        }
        return ans;
    }
}
