class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int>diff(n, 0);
        for(int i = 0 ; i < n ; i++){
            diff[i] = target[i] - nums[i];
        }
        diff.insert(diff.begin(), 0);
        long long ans = 0;
        for(int i = 1 ; i <= n ; i++){
            int req = -1 * (diff[i]);
            int prev = -1 * (diff[i-1]);
            int use_prev = abs(req - prev) ;
            int not_use_prev = abs(req);
            if(diff[i-1] > 0 && diff[i] > 0){
                if(diff[i-1] > diff[i]){
                    ans += 0;
                    continue;
                }
            }
            if(diff[i-1] < 0 && diff[i] < 0){
                if(abs(diff[i-1]) > abs(diff[i])){
                    ans += 0;
                    continue;
                }
            }
            ans += min(use_prev, not_use_prev);
        }
        return ans;
    }
};
