class Solution {
public:
    long long helper(vector<int> &v, bool cond) {
        if(cond) {
            for(auto &x : v) x =-x;
        }
        long long ret = 0, prev = 0;
        for(auto& it : v) ret += max(it - prev, 0ll), prev = it;
        return ret;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n  = nums.size();
        vector<int> v(n + 1);
        for(int i = 0; i < n; i++) {
            v[i] = (nums[i] - target[i]);
        }
        long long ans = 0;
        vector<int> tmp;
        for(int i=0; i<n+1; i++)
        {
            if(tmp.size() == 0) {
                tmp.push_back(v[i]);
                continue;
            }
            if(v[i]==0){
                ans+=helper(tmp,tmp.back()<0);
                tmp.clear();
            }
            else if((tmp.back()<0 && v[i]<0) || (tmp.back()>0 && v[i]>0))
            {
                tmp.push_back(v[i]);
            }
            else{
                ans+=helper(tmp,tmp.back()<0);
                tmp.clear();
                tmp.push_back(v[i]);
            }
        }
        return ans;
    }
};
