class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        dist = [t - n for t, n in zip(target, nums)]
        stack = []
        res = 0
        sign = 1
        for n in dist + [0]:
            if (n > 0) != sign:
                while stack:
                    res += stack.pop() - (stack[-1] if stack else 0)
                stack = []
                sign = (n > 0)
            while stack and stack[-1] > abs(n):
                cur = stack.pop()
                res += cur - max(abs(n), stack[-1] if stack else 0)
            stack.append(abs(n))
        return res
