class Solution {
public:
    // 1 1 1 2
    using ll = long long;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size(); // 数组长度

        vector<int> a(n);
        for (int i = 0; i < n; i++) {
            a[i] = nums[i] - target[i];
            cout << a[i] << " ";
        }
        cout << "\n";

        // 通过前一个和当前元素的差，来确定是否需要操作
        ll ops = abs(a[0]);
        for (int i = 1; i < n; i++) {
            // ops += max(0, min(abs(a[i] - a[i - 1]), abs(a[i])));
            // ops += min(
            //     max(a[i] - a[i - 1], 0), 
            //     max(a[i], 0));
            // cout << i << " " << ops << "\n";

            if (a[i] > 0 && a[i - 1] > 0) {
                ops += max(0, a[i] - a[i - 1]);
            } else if (a[i] < 0 && a[i - 1] < 0) {
                if (a[i] >= a[i - 1]) ops += 0;
                else ops += abs(a[i] - a[i - 1]);
            } else {
                ops += abs(a[i]);
            }
        }
        return ops;
    }
};
