class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> v(n);
        for(int i=0;i<n;i++)
        {
            int x=target[i]-nums[i];
            v[i]=x;
        }
        long long count=abs(v[0]);

        for(int i=1;i<n;i++)
        {
             if(v[i]>=0 && v[i-1]>=0)
             {
                if(v[i]>v[i-1])
                count+=(v[i]-v[i-1]);
             }
             else if(v[i]<=0 && v[i-1]>=0)
             {
                 count+=abs(v[i]);
             }
             else if(v[i]<=0 && v[i-1]<=0)
             {
                 if(v[i]<v[i-1])
                 {
                    count+=abs(v[i])-abs(v[i-1]);
                 }
             }
             else if(v[i]>=0 && v[i-1]<=0)
             {
                count+=v[i];
             }
        }
        return count;
    }
};
