/**
 * @param {number[]} nums
 * @param {number[]} target
 * @return {number}
 */
var minimumOperations = function(nums, target) {

    // 操作次数越多，越能完成。

    // 最少 操作次数。操作的子数组越长越好，找到子数组满足都小于target
    const n = nums.length

    const diff = Array.from({ length: n }, (_, i) => target[i] - nums[i])

    // console.log('diff', diff)
    let ans = Math.abs(diff[0])
    for (let i = 1; i < n; i++) {
        if (diff[i] === 0) {
            continue
        }
        if (diff[i] > 0 && diff[i - 1] > 0) { // 可以跟上一个一起-
            ans += Math.max(diff[i] - diff[i - 1], 0)
        } else if (diff[i] < 0 && diff[i - 1] < 0) { // 可以跟上一个一起+
            if (diff[i] < diff[i - 1]) {
                ans += diff[i - 1] - diff[i]
            } 
            // ans += Math.max(diff[i] - diff[i - 1], 0)
        } else {
            // 
            ans += Math.abs(diff[i])
        }
        // console.log(ans)
    }
    return ans
};
