#include<bits/stdc++.h>
using namespace std;
//#define int long long
#define x first
#define y second
#define pb push_back
#define all(x) (x).begin(), (x).end()
typedef long long ll;
typedef pair<int, int> PII;
const int N = 1e5 + 2, mod = 1e9 + 7; //= 998244353;
set<PII>le[N],ge[N];    
class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n=a.size();
        ll res=0;
        vector<int>da(n),db(n);
        da[0]=a[0];db[0]=b[0];
        for(int i=1;i<n;i++){
            da[i]=a[i]-a[i-1];
            db[i]=b[i]-b[i-1];
        }

        int add=0,sub=0;//后面加/减的最大数目
        for(int i=0;i<n;i++){
            if(da[i]<db[i]){
                //需要+
                int dif=db[i]-da[i];
                if(add>=dif)add-=dif;
                else{

                    sub+=dif-add;
                    res+=dif-add;
                     add=0;
                }
            }else{
                int dif=da[i]-db[i];
                if(sub>=dif)sub-=dif;
                else{

                    add+=dif-sub;
                    res+=dif-sub;
                    sub=0;
                }
            }
            //cout<<i<<' '<<sub<<' '<<add<<' '<<res<<endl;
        }
        return res;

    }
};
