class Solution {
public:
    vector<int> buildDifferenceArray(vector<int>& arr) {
        vector<int> diff(arr.size());
        diff[0] = arr[0];
        for (int i = 1; i < arr.size(); i ++ ) {
            diff[i] = arr[i] - arr[i - 1];
        }
        return diff;
    }
    #define LL long long
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> n1 = buildDifferenceArray(nums);
        vector<int> n2 = buildDifferenceArray(target);
        LL postive = 0;
        LL negtive = 0;

        // for(int x : n2) {
        //     cout << x << " ";
        // }
        for(int i = 0;i < nums.size() ;i++) {
            if(n2[i]-n1[i] > 0) postive += (n2[i]-n1[i]);
            if(n2[i]-n1[i] < 0) negtive += (-n2[i]+n1[i]);
        }
        return max(negtive, postive);

    }
};
