class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [target[i] - nums[i] for i in range(len(nums))]

        total_operations = 0
        current_segment_diff = 0

        for i in range(len(diff)):
            if i == 0 or (diff[i - 1] > 0 and diff[i] > 0) or (diff[i - 1] < 0 and diff[i] < 0):
                # Increment operations for the current segment
                additional_operations = abs(diff[i]) - abs(current_segment_diff)
                if additional_operations > 0:
                    total_operations += additional_operations
                current_segment_diff = diff[i]
            else:
                # Increment operations for a new segment
                total_operations += abs(diff[i])
                current_segment_diff = diff[i]

        return total_operations
