class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        new = [0] * len(nums)
        cum = [0] * len(nums)
        new[0] = -nums[0] +target[0]
        cum[0] = -nums[0] +target[0]
        for i in range(1,len(nums)):
            if target[i]-nums[i] == cum[i-1]:
                cum[i] = cum[i-1]
                new[i] = 0
            elif target[i] - nums[i] == 0:
                cum[i] = 0
                new[i] = 0
            elif cum[i-1] == 0:
                cum[i] = target[i] - nums[i]
                new[i] = target[i] - nums[i]
            elif (target[i] - nums[i]) * cum[i-1]<0:
                new[i] = target[i] - nums[i]
                cum[i] = target[i] - nums[i]
            else:
                cum[i] = target[i] - nums[i]
                if abs(target[i] - nums[i]) < abs(cum[i-1]):
                    new[i] = 0
                else:
                    new[i] = target[i] - nums[i] - cum[i-1]
        return sum([abs(i) for i in new])
