class Solution:
    def minimumOperations(self, c: List[int], d: List[int]) -> int:
        a=[]
        for i in range( 0, len(c)):
            a.append(d[i]-c[i])
        a.append(0)
        ans=0
        for i in range( 1, len(a)):
            if a[i-1]>0:
                if a[i-1] >a[i]: ans+=a[i-1]-max(  0,a[i]  )

            if a[i-1]<0:
                if a[i-1] <a[i]:
                    if a[i]>0:ans+=abs(  a[i-1]  )
                    else: ans+=abs(  a[i-1]  )-abs(  a[i]  )
        return  ans
