class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        vector<int>tar;
        for(int i=0;i<nums.size();i++)
            tar.push_back(target[i]-nums[i]);

        vector<vector<int>> v;
        vector<int> temp;
        bool f=false;
        if(tar[0]>=0)
            f=true;
        for(int i=0;i<tar.size();i++){
            if(tar[i]>=0)
            {
                if(f)
                temp.push_back(abs(tar[i]));
                else
                {
                    v.push_back(temp);
                    temp.clear();
                    temp.push_back(abs(tar[i]));
                    f=true;
                }
            }
            else
            {
                   if(!f)
                temp.push_back(abs(tar[i]));
                else
                {
                    v.push_back(temp);
                    temp.clear();
                    temp.push_back(abs(tar[i]));
                    f=false;
                }
            }

        }
        v.push_back(temp);

           long long totalOperations =0;

        for(int i=0;i<v.size();i++)
        {
            //cout<<"a";
            long long t=v[i][0];
            for(int j=1;j<v[i].size();j++){

                if(v[i][j]>v[i][j-1])
                    t+=v[i][j]-v[i][j-1];

            }
            totalOperations+=t;
        }

        for(int i=0;i<v.size();i++)
        {

            for(int j=0;j<v[i].size();j++){
               cout<<v[i][j]<<" ";

            }
            cout<<endl;
        }
        return totalOperations;
    }


};
