class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        vector<int>vec(nums.size(),0);
        for (int i=0;i<nums.size();i++)
        {
            vec[i]=tar[i]-nums[i];
        }
        int hui=0;
        long long  mama=0;
        long long hola=0;
        hola= abs(vec[0]);
        mama=hola;
        if(vec[0]>=0)
        hui=1;
        for(int i=1;i<nums.size();i++)
        {
            if(vec[i]<0 && hui==1)
            {
                hola+= abs(vec[i]);
                mama= abs(vec[i]);
                hui=0;
                continue;
            }
            if(vec[i]>=0 && hui==0)
            {
                hola+= abs(vec[i]);
                mama= abs(vec[i]);
                hui=1;
                continue;
            }
            if(mama >= abs(vec[i]))
            {

                mama=abs(vec[i]);

                continue;
            }
            else
            {

                hola+= abs(vec[i])-mama;
                mama=abs(vec[i]);

            }


        }
        return hola;
    }
};
