class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        vector<long long> diffa(n), diffb(n);
        for (int i = 1; i < n; i++) {
            diffa[i] = a[i] - a[i - 1];
            diffb[i] = b[i] - b[i - 1];
        }
        diffa[0] = a[0]; diffb[0] = b[0];
        long long ans = 0;
        long long minus = 0, plus = 0;
        for (int i = 0; i < n; i++) {
            long long dis = diffb[i] - diffa[i];
            if (dis < 0) {
                // -1, +1
                dis *= -1;
                if (minus >= dis) {
                    minus -= dis;
                }
                else {
                    dis -= minus;
                    minus = 0;
                    ans += dis;
                    plus += dis;
                }
            }
            else {
                // +1, -1
                if (plus >= dis) {
                    plus -= dis;
                }
                else {
                    dis -= plus;
                    plus = 0;
                    ans += dis;
                    minus += dis;
                }
            }


        }

        return ans;
    }
};
