class Solution {
    int n;
    public long minimumOperations(int[] nums, int[] target) {
        this.n=target.length;
        long res=0;

        long add=0,minus=0;
        for(int i=0;i<n;i++){
            long c=nums[i]-target[i];
            if(c<0){
                minus=0;
                if(add<=Math.abs(c)){
                    res+=Math.abs(c)-add;
                    add=Math.abs(c);
                }else{
                    add=Math.abs(c);
                }
            }else if(c>0){
                add=0;
                if(minus<c){
                    res+=c-minus;
                    minus=c;
                }else{
                    minus=c;
                }

            }else{
                add=0;
                minus=0;
            }
            //System.out.println(i+":"+res+","+add+","+minus);
        }

        return res;
    }
}
