class Solution {
public:
    #define ll long long
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = target.size();
        vector <int> dp (n);
        vector <int> diff (n);
        for (int i = 0; i < n; i++){
            diff[i] = target[i] - nums[i];
        }

        dp[0] = abs (diff[0]);

        for (int i = 1; i < n; i++){
            if (diff[i] > 0 && diff[i - 1] > 0){
                if (diff[i] > diff[i - 1]){
                    dp[i] = (diff[i] - diff[i - 1]);
                }
            }
            else if (diff[i] < 0 && diff[i - 1] < 0){
                if (abs (diff[i]) > abs (diff[i - 1])){
                    dp[i] =  abs (diff[i]) - abs (diff[i - 1]);
                }
            }
            else{
                dp[i] = abs (diff[i]);
            }
        }

        ll sum = 0;
        for (auto ele : dp) sum += ele;
        return sum;
    }
};
