class Solution
{
public:
    long long solve(vector<long long > &v)
    {
        long long ct = 0;
        int n = v.size();
        for (int i = 0; i < n; i++)
        {
            v[i] = abs(v[i]);
        }

        vector<long long > prf(n, 0);
      long long prev=0;
         for (int i = 0; i < n; i++)
        {
                prf[i]=v[i]-prev;
                prev=v[i];
        }

           for (int i = 0; i < n; i++)
        {
            if(prf[i]>0){
                ct+=prf[i];
            }
        }

        return ct;

    }
    long long minimumOperations(vector<int> &nums, vector<int> &target)
    {
        long long ans = 0;
        int n = nums.size();
        vector<long long > v(n, 0);
        for (int i = 0; i < nums.size(); i++)
        {
            v[i] = target[i] - nums[i];
        }

        vector<vector<long long >> vt;
        vector<long long > pos;
        vector<long long > neg;
        for (int i = 0; i < n; i++)
        {
            if (v[i] > 0)
            {
                if (neg.size() > 0)
                {
                    vt.push_back(neg);
                }
                neg = {};
                pos.push_back(v[i]);
            }
            else if (v[i] < 0)
            {
                if (pos.size() > 0)
                {
                    vt.push_back(pos);
                }
                pos = {};
                neg.push_back(v[i]);
            }
             else
            {
                if (pos.size() > 0)
                {
                    vt.push_back(pos);
                }
                pos = {};
                if (neg.size() > 0)
                {
                    vt.push_back(neg);
                }
                neg = {};
            }
        }

        if (pos.size() > 0)
        {
            vt.push_back(pos);
        }
        if (neg.size() > 0)
        {
            vt.push_back(neg);
        }
        long long ct = 0;
        for (auto it : vt)
        {
            vector<long long >xt=it;
            long long sum = solve(xt);
            ct += sum;
        }
        return ct;
    }
};
