func minimumOperations(nums []int, target []int) int64 {
    n:=len(nums)
    //
    // 0,3,-1,-9,3,1,5,3,
    for i:=range target{
        target[i] -= nums[i]
    }
    dp:=make([]int,n)
    dp[0] = abs(target[0])
    for i:=1;i<n;i++{
        if check(target[i],target[i-1]) {
            if target[i-1] >0 {
                if target[i-1] >= target[i]{
                    dp[i] = dp[i-1]
                }else{
                    dp[i] = dp[i-1] + target[i] - target[i-1]
                }  
            }else{
                if target[i-1] <= target[i]{
                    dp[i] = dp[i-1]
                }else{
                    dp[i] = dp[i-1] + target[i-1] - target[i]
                }              
            }
        }else{
                dp[i] = dp[i-1]+abs(target[i])
                }
    }
   return int64(dp[n-1])
}
func abs(x int)int{
    if x<0{
        return -x
    }
    return x
}
func check(x int, y int)bool{
    return x*y>0
}
