class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> diff(n,0);
        for(int i=0;i<n;i++){
            diff[i]=target[i]-nums[i];
            // cout<<diff[i]<<" ";
        }
        // cout<<'\n';
        long long ans=0, curr=0;
        for(auto i1:diff){
            long long i=i1;
            if(i==0) curr=0;
            else if(i>0) {if(i>curr){ans=ans+min(i,i-curr);curr=i;} else{curr=i;}}
            else {if(i<curr){ans=ans+min(curr-i,-i);curr=i;} else{curr=i;}}
            // cout<<ans<<" "<<curr<<endl;
        }
        return ans;
    }
};
