class Solution {
public:
    long long solve (int start ,int end ,vector<int>&nums){
        long long ans=0;
        int  count =0;
        for (int i=start;i<=end ;i++){
            if(nums[i]>count){
                ans+=nums[i]-count;
                count=nums[i];
            }else{
                count=nums[i];
            }
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        for(int i=0;i<n;i++){
            nums[i]=target[i]-nums[i];
        }
        int start=0;
        int sign=0;
        for(int i=0;i<n;i++){
            if (nums[i]<0&&(sign==0||sign==-1)){
                sign=-1;
                nums[i]=abs(nums[i]);
                continue;
            }
            else if(nums[i]<0&& sign==1){
                ans+=solve(start,i-1,nums);
                nums[i]=abs(nums[i]);
                sign=-1;
                start=i;
            }
            else if(nums[i]>=0&&(sign==1||sign ==0)){
                sign=1;
                continue;

            }else if(nums[i]>=0&&(sign==-1)){
                ans+=solve(start,i-1,nums);
                sign=1;
                start=i;
            }
        }
        ans+=solve (start,n-1,nums);
        return ans;
    }
};
