class Solution {
public:
    long long calc(vector<int>& A) {
        long long res = 0, pre = 0;
        for (int a: A) {
            res += max(a - pre, 0LL);
            pre = a;
        }
        return res;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> tmp;
        bool sm=false;
        int n=nums.size();
        long long res=0;
        for (int i=0; i<n; ++i) {
            if (target[i]==nums[i]) {
                tmp.push_back(0);
            } else if (target[i]<nums[i]) {
                if (tmp.size()==0||sm) {
                    tmp.push_back(nums[i]-target[i]);
                    sm=true;
                } else {
                    res+=calc(tmp);
                    tmp={nums[i]-target[i]};
                    sm=true;
                }
            } else {
                if (tmp.size()==0||!sm) {
                    tmp.push_back(target[i]-nums[i]);
                    sm=false;
                } else {
                    res+=calc(tmp);
                    tmp={target[i]-nums[i]};
                    sm=false;
                }
            }
        }
        if (tmp.size()>0) {
            res+=calc(tmp);
        }
        return res;
    }
};
