class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<long long int>v(n),z(n);
        for(int i=0;i<n;i++)
        {
            v[i]=target[i]-nums[i];
            z[i]=-v[i];
        }
        long long ans=0,ct=0;
        for(int i=0;i<n;i++)
        {
            if((ct>=0 && v[i]>=0) ||(ct<=0 && v[i]<=0))
            {
                if(abs(v[i])<=abs(ct))
                {
                    ct=v[i];
                }
                else
                {
                    ans+=abs(v[i])-abs(ct);
                    ct=v[i];
                }
            }
            else
            {
                ans+=abs(v[i]);
                ct=v[i];
            }
        }
        return ans;

    }
};
