class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        for(int i = 0; i < nums.length; i++){
            nums[i] = target[i] - nums[i];
        }
        long res = 0;
        for(int i = 0; i < nums.length; i++){
            if(nums[i] != 0){
                if(nums[i] > 0){
                    while(nums[i] > 0){
                        int m = nums[i];int j = i;
                        for(; j < nums.length; j++){
                            if(nums[j] <= 0){
                                break;
                            }
                            if(nums[j] < m){
                                m = nums[j];
                            }
                        }
                        for(int k = i; k < j; k++){
                            nums[k] -= m;
                        }

                        res+=m;
                    }
                }else{
                    // while(nums[i] < 0){
                    //     for(int j = i; j < nums.length; j++){
                    //         if(nums[j] < 0){
                    //             nums[j] ++;
                    //         }else{
                    //             break;
                    //         }
                    //     }
                    //     res++;
                    // }
                    while(nums[i] < 0){
                        int m = nums[i];int j = i;
                        for(; j < nums.length; j++){
                            if(nums[j] >= 0){
                                break;
                            }
                            if(nums[j] > m){
                                m = nums[j];
                            }
                        }
                        for(int k = i; k < j; k++){
                            nums[k] -= m;
                        }
                        // System.out.print(m);
                        res-=m;
                    }
                }
            }
        }
        return res;
    }
}
