class Solution {
public:
    using ll = long long;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans = 0;
        int n = nums.size();
        int mx = 0, mi = 0; 
        for (int i = 0; i < n; i++) {
            int p = nums[i] - target[i];
            if (p >= mi && p <= mx) {
                mi = max(p, mi);
                mi = min(0, mi);
                mx = min(p, mx);
                mx = max(0, mx);
            } else if (p > mx) {
                ans += p - mx;
                mx = p;
                mi = 0;
            } else {
                ans += mi - p;
                mi = p;
                mx = 0;
            }
        }
        return ans;
    }
};
