class Solution {
public:
    #define ll long long 
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        ll n= nums.size();

        vector<ll>df(n);
        for(ll i=0;i<n;i++){
            df[i]=tar[i]-nums[i];
        }

        ll c=0;
        ll mx=0;

        for(ll i=0;i<n;i++){
            if(df[i]<0){
                mx=0;
            }
            else{
                if(df[i]>mx){
                    c+=(df[i]-mx);
                }
                mx= df[i];
            }
            df[i]*=(-1);
        }

        mx=0;    
        for(ll i=0;i<n;i++){
            if(df[i]<0){
                mx=0;
            }
            else{
                if(df[i]>mx){
                    c+=(df[i]-mx);
                }
                mx= df[i];
            }
            df[i]*=(-1);
        }

        return c;
    }
};
