class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n=len(nums)
        a=[x-y for x,y in zip(nums,target)]
        print(a)
        st=[]
        ans=0
        for i in range(n):
            if st and st[-1]*a[i]<=0:
                ans+=abs(st[-1])
                st=[a[i]]
                # print(st)
                continue
            if st and abs(st[-1])>abs(a[i]):
                ans+=abs(st[-1])-abs(a[i])
            while st and abs(st[-1])>abs(a[i]):
                st.pop()
            st.append(a[i])
        ans+=abs(st[-1])
        return ans
