class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        prev=0
        diff=0
        st=0
        ans=0
        for i in range(len(nums)):
            # print(st,prev,diff,ans)
            if prev==0:
                if nums[i]<target[i]:
                    prev=1
                    diff=target[i]-nums[i]
                    st+=1
                elif nums[i]>target[i]:
                    prev=-1
                    diff=-target[i]+nums[i]
                    st+=1  
                else:
                    continue      
            else :
                if nums[i]<target[i] and prev==1 and diff==target[i]-nums[i]   or nums[i]>target[i] and prev==-1 and diff==-target[i]+nums[i] :
                    st+=1
                    continue
                elif nums[i]<target[i] and prev==1:
                    if target[i]-nums[i]>diff:
                        st=1
                        diff=target[i]-nums[i]
                    else:
                        ans+=diff-target[i]+nums[i]
                        diff= target[i]-nums[i]  
                    continue    
                elif nums[i]>target[i] and prev==-1:
                    if -target[i]+nums[i]>diff:
                        st=1
                        diff=-target[i]+nums[i]
                    else:
                        ans+=diff+target[i]-nums[i]
                        diff= -target[i]+nums[i]  
                    continue                     
                elif nums[i]==target[i]:
                    ans+=diff 
                    prev=0
                    st=0
                    diff=0

                else:
                    ans+=diff 
                    if nums[i]<target[i]:
                        prev=1
                        diff=target[i]-nums[i]
                        st+=1
                    else:
                        prev=-1
                        diff=-target[i]+nums[i]
                        st+=1  

        return ans+diff
