class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        N = len(target)
        dec_ops = 0
        inc_ops = 0
        total_ops = 0
        for i in range(N):
            if target[i] > nums[i]:
                diff = target[i] - nums[i]
                if diff > inc_ops:
                    total_ops += diff - inc_ops
                inc_ops = diff
                dec_ops = 0
            else:
                diff = nums[i] - target[i]
                if diff > dec_ops:
                    total_ops += diff - dec_ops
                dec_ops = diff
                inc_ops = 0
            # print(i, total_ops)
        return total_ops
