class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        int pre=0; long long ans=0;
        for(int i=0;i<n;++i) {
            if(target[i]>nums[i]) {
                if(pre<0) pre=0;
                ans+=max(0, target[i]-nums[i]-pre);
            } else if(target[i]<nums[i]) {
                if(pre>0) pre=0;
                ans-=min(0, target[i]-nums[i]-pre);
            }
            pre=target[i]-nums[i];
        }
        return ans;
    }
};
