class Solution {
public:

    long long r(vector<int> &v,int i,int j){
        //op(v,"      ",i,j);
        int len = (j-i+1);
        int n=v.size();
        if((i>=n)||(j>=n)) return 0;
        if(len==0) return 0;
        if(len==1) return v[i];

        long long mn=INT_MAX;
        for(int k=i;k<=j;++k){
            if(v[k]) mn=min(mn,v[k]*1LL);
        }
        for(int k=i;k<=j;++k){
            if(v[k]) v[k]-=mn;
        }

        int s=i,e=i;
        for(int k=i;k<=j;++k){
            if(v[k]==0){
              mn+=r(v,s,e);
              s=(k+1);
              e=(k+1);  
            }
            else{
                s=min(s,k);
                e=max(e,k);
            }
        }
        mn+=r(v,s,e);

        return mn+0;
    }


    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> diff(n);
        for(int i=0;i<n;++i){
            diff[i] = target[i] - nums[i];
        }
        //op(diff);
        long long ans=0;
        vector<int> v;
        for(int i=0;i<n;++i){
            if(v.size()){
                long long x = (v.back()*1LL)*diff[i];
                int sz=v.size();
                if(x<=0) {
                    //op("v",v);
                    if(v[0]<0){
                        for(auto &i : v) i*=-1;
                    }
                    ans+=r(v,0,sz-1);
                    v.clear();
                }
            }
            v.push_back(diff[i]);

        }
        if(v.size()){
            int sz=v.size();
            //op("v",v);
            if(v[0]<0){
                for(auto &i : v) i*=-1;
            }
            ans+=r(v,0,sz-1);
            v.clear();
        }
        return ans;
    }
};
