class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = []
        for i in range(n):
            diff.append(target[i] - nums[i])

        ans = abs(diff[0])
        for i in range(1,n):

            if diff[i] * diff[i-1] > 0:
                if abs(diff[i]) > abs(diff[i-1]):
                    ans += abs(diff[i]) - abs(diff[i-1])

            elif diff[i]*diff[i-1] <= 0:
                ans += abs(diff[i])

        return ans
