class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        int n1[n+5],n2[n+5];
        memset(n1,0,sizeof(n1));
        memset(n2,0,sizeof(n2));
        n1[0]=nums[0],n2[0]=target[0];
        for(int i=1;i<n;i++){
            n1[i]=nums[i]-nums[i-1];
            n2[i]=target[i]-target[i-1];
        }
        long long ans=0,neg=0,pos=0;
        for(int i=0;i<n;i++){
            int t=n1[i]-n2[i];
            if(t>0)pos+=t;
            if(t<0)neg+=t;


        }
        ans=min(abs(pos),abs(neg));
        ans+=abs(pos+neg);
        return ans;

    }
};
