class Solution {
public:
    int cal(int a,int b){
        if(a > 0)return a-b;
        return b-a;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>dis(nums.size());
        for(int i = 0;i<nums.size();++i){
            dis[i] = target[i] - nums[i];
        }
        vector<int>stk;
        long long res = 0;
        for(int i = 0;i<nums.size();++i){
            while(!stk.empty() && 1ll * stk.back() * dis[i] <= 0)stk.pop_back();
            if(stk.empty()){
                res += abs(dis[i]);
            }else if(cal(dis[i], stk.back())>=0){
                res += abs(dis[i] - stk.back());
                while(!stk.empty() && cal(dis[i], stk.back())>=0)stk.pop_back();
            }
            // cout<<res<<"??"<<endl;
            stk.push_back(dis[i]);
        }
        // 0 3 -1 -9 3 1 5 3 4 2
        return res;
    }
};
