class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        long long res = 0;
        int n = nums.size();
        vector<long long> target(n);
        for(int i = 0; i < n; ++i) {
            target[i] = t[i] - nums[i];
        }
        int pre = target[0];
        res += abs(pre);
        for(int i = 1; i < n; ++i) {
            if(pre >= 0) {
                while(i < n && target[i] >= 0) {
                    if(target[i] <= pre){
                        pre = target[i];
                    } else {
                        res += (target[i] - pre);
                        pre = target[i];
                    }
                    ++i;
                }
                if(i == n) {
                    break;
                } else {
                    pre = target[i];
                    res += abs(target[i]);
                }
            } else {
                while(i < n && target[i] <= 0) {
                    if(target[i] >= pre) {
                        pre = target[i];
                    } else {
                        res += abs(target[i] - pre);
                        pre = target[i];
                    }
                    ++i;
                }
                if(i == n) {
                    break;
                } else {
                    pre = target[i];
                    res += abs(target[i]);
                }
            }
        }
        return res;
    }
};
