class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) 
    {
        vector<int> n,t;
        n.push_back(nums[0]);
        t.push_back(target[0]);
        for (int i=1;i<nums.size();++i)
        {
            n.push_back(nums[i]-nums[i-1]);
            t.push_back(target[i]-target[i-1]);
        }   
        int add=0,sub=0;
        long long res=0;
        for (int i=0;i<nums.size();++i)
        {
            int now=t[i]-n[i];
            if (now>0)
            {
                if (add>=now)
                    add-=now;
                else
                {
                    now-=add;
                    add=0;
                    res+=now;
                    sub+=now;
                }
            }
            else if (now<0)
            {
                now=-now;
                if (sub>=now)
                    sub-=now;
                else
                {
                    now-=sub;
                    sub=0;
                    res+=now;
                    add+=now;
                }
            }
        }
        return res;
    }
};
