class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long result = 0;
        int n = nums.size();
        vector<int> difference(n,0);
        for(int i = 0; i < n; i++) difference[i] = target[i] - nums[i];
        int l = 0;
        while(l < n){
            int base = difference[l];
            int r = l;
            if(base > 0){
                while(r < n && difference[r] > 0) {
                    base = min(difference[r], base);
                    r++;
                }
                while(r > l) difference[--r] -= base;
                result += base;
            }
            else if(base < 0){
                while(r < n && difference[r] < 0) {
                    base = max(difference[r], base);
                    r++;
                }
                while(r > l) difference[--r] -= base;
                result -= base;
            }
            else l++;
        }
        return result;
    }
};
