class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i = n - 1; i; i--) {
            nums[i] -= nums[i - 1];
            target[i] -= target[i - 1];
        }
        long long ret = 0, s1 = 0, s2 = 0;
        for (int i = 0; i < n; i++) {
            if (nums[i] < target[i]) {
                long long d = target[i] - nums[i], t = min(d, s1);
                d -= t;
                s1 -= t;
                ret += d;
                s2 += d;
            }
            else {
                long long d = nums[i] - target[i], t = min(d, s2);
                d -= t;
                s2 -= t;
                ret += d;
                s1 += d;
            }
        }
        return ret;
    }
};
