class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();

        vector<long long> cur(n);
        for(int i=0;i<n;i++)
            cur[i]=target[i]-nums[i];

        long long prev=0;
        long long ans=0;
        for(int i=0;i<n;i++){
            if(cur[i]>=0&&prev>=0){
                if(prev<cur[i]){
                  ans+=cur[i]-prev;
                }

                    prev=cur[i];

            }
            else if(cur[i]<=0&&prev<=0){
                if(prev>cur[i]){
                    ans+=prev-cur[i];
                }

                    prev=cur[i];

            }
            else{
                prev=cur[i];
                ans+=abs(cur[i]);

            }
        }


        return ans;
    }
};
